mapper.delete:
DELETE FROM note WHERE note_no = #{noteNo}
				 AND note_div = #{noteDiv}
				 AND employee_id = #{employeeId}

mapper.update:
UPDATE note SET read = #{read}, read_dt = sysdate
WHERE note_no = #{noteNo}
AND note_div = #{noteDiv}
AND employee_id = #{employeeId}

UPDATE note SET note_div = 3
WHERE note_no = #{noteNo}
AND note_div = #{noteDiv}
AND employee_id = #{employeeId}
